import React, { useState } from 'react';
import axios from 'axios';
const UpdeteData=()=> {
    const [updateData, setUpdateData] = useState({ /* Your updated data here */ });
    const id = 'your-document-id'; // Replace with the actual document ID
  
    const handleUpdateData = async () => {
      try {
        const response = await axios.put(`http://localhost:3000/todoapp/${id}`, updateData);
        console.log(response.data);
      } catch (error) {
        console.error('Error updating data:', error);
      }
    };
  
    return (
      <div>
        {/* Your form or input fields to capture updated data */}
        <button onClick={handleUpdateData}>Update Data</button>
      </div>
    );
  };


export default UpdeteData;
